describe('ListSelectionToolbar', () => {
    let $state
    let $translate
    let customActionConfig
    let dialogService
    let listService
    let ListSelectionToolbar
    let ctrl

    beforeEach(() => {
        $state = {}

        $translate = {}

        customActionConfig = {
            getTextButtons: sinon.stub().returns([]),
            getIconButtons: sinon.stub().returns([])
        }

        dialogService = {}

        listService = {}

        ListSelectionToolbar = require('../../../../main/core/list/selectionToolbar/ListSelectionToolbar')

        ctrl = new ListSelectionToolbar.controller($state, $translate, customActionConfig, dialogService, listService)

        ctrl.$onInit()
    })

    it('should initialize', () => {
        ctrl.alternateTextButtons.should.deep.equal([])
        ctrl.alternateIconButtons.should.deep.equal([])
    })
})
