describe('ModalActionIcons', () => {
    let ModalActionIcons
    let ctrl

    beforeEach(() => {
        ModalActionIcons = require('../../../main/core/modalActionComponent/ModalActionIcons')

        ctrl = new ModalActionIcons.controller()
        ctrl.toggleActive = sinon.stub().returns()
    })

    it('should toggle active items a default way ', () => {
        let row = {
            active: true
        }

        ctrl.toggleActiveItem(row)

        row.active.should.equal(false)
    })

    it('should toggle active if a custom function is defined', () => {
        ctrl.toggleActive = sinon.stub().returns(true)

        let row = {
            active: true
        }

        ctrl.toggleActiveItem(row)

        row.active.should.equal(true)
    })
})
