describe('authConfigProvider', function() {
    var authConfigProvider

    beforeEach(function() {
        var constructor = require('../../../main/core/providers/authConfigProvider')
        authConfigProvider = {}
        _.bind(constructor, authConfigProvider)()
    })

    it('should have peoplenet stuff', function() {
        authConfigProvider.config.adminOrgs.Administrative.key.should.equal('peoplenet')
        authConfigProvider.config.adminOrgs.Administrative.name.should.equal('Peoplenet')

        var authConfig = authConfigProvider.$get()

        authConfig.adminOrgs.Administrative.key.should.equal('peoplenet')
        authConfig.adminOrgs.Administrative.name.should.equal('Peoplenet')
    })

    it('should $get a readonly copy', function() {
        var val1 = authConfigProvider.$get()

        val1.blah = 'blah'

        var val2 = authConfigProvider.$get()

        val1.should.not.deep.equal(val2)
    })
})
