describe('candyBarConfigProvider', function() {
    var candyBarConfigProvider

    beforeEach(function() {
        var constructor = require('../../../main/core/providers/candyBarConfigProvider')
        candyBarConfigProvider = {}
        _.bind(constructor, candyBarConfigProvider)()
    })

    it('should support adding items', () => {

        var linkItem = {
            name: 'blah',
            src: 'blahsrc',
            link: 'www.bl.ah'
        }

        var themeItem = {
            name: 'test',
            src: 'testsrc'
        }

        candyBarConfigProvider.addLinkItem(linkItem)

        candyBarConfigProvider.addThemeItem(themeItem)

        var candyBarConfig = candyBarConfigProvider.$get()

        candyBarConfig.themes.should.deep.equal([themeItem])
        candyBarConfig.items.should.deep.equal([linkItem])
    })
})
