describe('customActionConfigProvider', () => {
    let customActionConfigProvider

    beforeEach(() => {
        let constructor = require('../../../main/core/providers/customActionConfigProvider')
        customActionConfigProvider = {}
        _.bind(constructor, customActionConfigProvider)()
    })

    it('should support adding items', () => {
        let context = 'myList'
        let toolbar = 'default'

        let textButton = {
            text: 'Edit',
            click: () => {},
            permission: 'ROLE_OUT'
        }

        customActionConfigProvider.addTextButton(context, toolbar, textButton)

        let textButtons = customActionConfigProvider.$get().getTextButtons(context, toolbar)
        textButtons.should.deep.equal([textButton])

        let iconButton = {
            icon: 'edit',
            click: () => {},
            permission: 'ROLE_ROLE_ROLE_YOUR_BOAT'
        }

        customActionConfigProvider.addIconButton(context, toolbar, iconButton)

        let iconButtons = customActionConfigProvider.$get().getIconButtons(context, toolbar)
        iconButtons.should.deep.equal([iconButton])
    })
})
