describe('mapConfigProvider', function() {
    var mapConfigProvider

    beforeEach(function() {
        var constructor = require('../../../main/core/providers/mapConfigProvider')
        mapConfigProvider = {}
        _.bind(constructor, mapConfigProvider)()
    })

    it('should allow setting things at config time only', function() {
        mapConfigProvider.config.thing1 = 'thing2'

        //should allow retrieving at service time
        var mapConfig = mapConfigProvider.$get()
        mapConfig.thing1.should.equal('thing2')

        //should be one-way
        mapConfig.blah = 'blah'
        should.not.exist(mapConfigProvider.config.blah)
    })
})
