let LoadingService = require('../../../main/core/services/LoadingService')

describe('LoadingService', () => {
    let svc

    beforeEach(() => {
        svc = new LoadingService()
    })

    it('should support setting loading', () => {
        svc.loading('abc').should.be.true
        svc.loading().should.be.true

        svc.loading(false).should.be.false
        svc.loading().should.be.false

        svc.loading(true).should.be.true
        svc.loading().should.be.true
    })

    it('should support setting asyncLoading', () => {
        svc.asyncLoading('abc').should.be.true
        svc.asyncLoading().should.be.true

        svc.asyncLoading(false).should.be.false
        svc.asyncLoading().should.be.false

        svc.asyncLoading(true).should.be.true
        svc.asyncLoading().should.be.true
    })
})
