describe('loginRouteConfig', function() {
    var $stateProvider
    var routeHelper

    beforeEach(function() {
        $stateProvider = {}
        $stateProvider.state = sinon.stub().returns($stateProvider)

        routeHelper = {}
        routeHelper.resolveCurrentUser = sinon.stub()
        routeHelper.currentUser = sinon.stub()

        require('../../../main/login/config/loginRouteConfig')($stateProvider, routeHelper)
    })

    it('should provide routes', function() {
        $stateProvider.state.should.have.callCount(6)
        $stateProvider.state.should.have.been.calledWith('logout')
        $stateProvider.state.should.have.been.calledWith('auth')
        $stateProvider.state.should.have.been.calledWith('auth.login')
        $stateProvider.state.should.have.been.calledWith('auth.forgotpassword')
        $stateProvider.state.should.have.been.calledWith('auth.resetpassword')
        $stateProvider.state.should.have.been.calledWith('auth.activateaccount')
    })
})
