describe('permissionNavigationConfig', () => {
    let navigationProvider

    beforeEach(() => {
        navigationProvider = {}
        navigationProvider.addMenuItem = sinon.stub()

        require('../../../main/permission/config/permissionNavigationConfig')(navigationProvider)
    })

    it('should register for navigation menu', () => {
        navigationProvider.addMenuItem.should.have.been.calledWith('link', 'permission.common.menu')
    })
})
