describe('RoleCard', () => {
    var $mdMedia
    var currentUser
    var component
    var ctrl

    beforeEach(() => {
        $mdMedia = {media: true}

        currentUser = {}
        currentUser.hasPermission = sinon.stub().returnsArg(0)

        component = require('../../../main/role/components/RoleCard')
        ctrl = new component.controller($mdMedia)

        ctrl.currentUser = currentUser
    })

    it('should expose some variables', () => {
        ctrl.$onInit()

        ctrl.$mdMedia.should.equal($mdMedia)
        ctrl.canWriteRoles.should.equal('ROLE_ROLE_WRITE')
        ctrl.canDeleteRoles.should.equal('ROLE_ROLE_DELETE')
    })
})
