describe('roleNavigationConfig', function() {
    var navigationProvider

    beforeEach(function() {
        navigationProvider = {}
        navigationProvider.addMenuItem = sinon.stub()

        require('../../../main/role/config/roleNavigationConfig')(navigationProvider)
    })

    it('should register for navigation menu', function() {
        navigationProvider.addMenuItem.should.have.been.calledWith('link', 'role.common.menu')
    })

})
