describe('UserDetailsContainer', () => {
    var component
    var ctrl
    var userTabConfig

    beforeEach(() => {
        userTabConfig = {
            detailsTabs: 'abc'
        }

        component = require('../../../main/user/components/UserDetailsContainer')
        ctrl = new component.controller(userTabConfig)
        ctrl.$onInit()
    })

    it('should initialize', () => {
        ctrl.tabs.should.equal(userTabConfig.detailsTabs)
    })
})
