describe('UserEditContainer', () => {
    var component
    var ctrl
    var userTabConfig
    var currentUser
    var $state

    beforeEach(() => {
        userTabConfig = {
            editTabs: 'abc'
        }

        currentUser = {orgType: 'something'}

        $state = {}
        _.set($state, '$current.locals.globals', {
            currentUser
        })

        component = require('../../../main/user/components/UserEditContainer')
        ctrl = new component.controller($state, userTabConfig)
        ctrl.$onInit()
    })

    it('should initialize', () => {
        ctrl.tabs.should.equal(userTabConfig.editTabs)
        ctrl.isAdmin.should.be.false
    })

    it('it should recognize admins', () => {
        currentUser.orgType = 'adMINIstRATive'

        ctrl.$onInit()

        ctrl.isAdmin.should.be.true
    })
})
