describe('userNavigationConfig', function() {
    var navigationProvider

    beforeEach(function() {
        navigationProvider = {}
        navigationProvider.addMenuItem = sinon.stub()

        require('../../../main/user/config/userNavigationConfig')(navigationProvider)
    })

    it('should register for navigation menu', function() {
        navigationProvider.addMenuItem.should.have.been.calledWith('link', 'user.common.menu')
    })

})
