describe('userTabConfigProvider', function() {
    var userTabConfigProvider

    beforeEach(function() {
        var constructor = require('../../../main/user/providers/userTabConfigProvider')
        userTabConfigProvider = {}
        _.bind(constructor, userTabConfigProvider)()
    })

    it('should allow adding edit tabs', function() {
        userTabConfigProvider.addEditTab('abc', 'def')

        let userTabConfig = userTabConfigProvider.$get()

        userTabConfig.editTabs.should.deep.equal([{
            label: 'abc',
            template: 'def'
        }])
    })

    it('should allow adding details tabs', function() {
        userTabConfigProvider.addDetailsTab('abc', 'def')

        let userTabConfig = userTabConfigProvider.$get()

        userTabConfig.detailsTabs.should.deep.equal([{
            label: 'abc',
            template: 'def'
        }])
    })
})
